"""
    geodesic_odes!(dx::Vector{<:Real}, x::Vector{<:Real}, geometry::Geometry)

Calculate the derivatives of ``x^μ`` and ``p_i`` with respect to the proper time, returned
in this order. Expresssions are exported from Mathematica.
"""
@fastmath function geodesic_odes!(dx::Vector{<:Real}, x::Vector{<:Real}, geometry::Geometry)
    # Unpack the coordinates
    r, θ, ϕ, p_r, p_θ, p_ϕ = @view x[2:end]
    @unpack a = geometry

    # Precompute basic trigonometric functions.
    s_θ = sin(θ)
    c_θ = cos(θ)

    # Precompute further trigonometric functions using trigonometric identities.
    s_2θ = 2 * s_θ * c_θ
    c_2θ = c_θ * c_θ - s_θ * s_θ
    t_θ = s_θ / c_θ
    t_θ2 = t_θ * t_θ

    # Precompute powers of the BH spin
    a2 = a * a
    a3 = a * a2
    a4 = a * a3
    a6 = a3 * a3

    # Precompute powers of the coordinates
    r2 = r * r
    r3 = r2 * r
    r4 = r2 * r2
    r5 = r4 * r
    r6 = r3 * r3

    s_θ2 = s_θ * s_θ
    s_θ3 = s_θ2 * s_θ
    s_2θ2 = s_2θ * s_2θ

    c_θ2 = c_θ * c_θ
    c_θ4 = c_θ2 * c_θ2
    c_θ6 = c_θ4 * c_θ2

    p_r2 = p_r * p_r
    p_θ2 = p_θ * p_θ
    p_ϕ2 = p_ϕ * p_ϕ

    # Various functions appearing in the Kerr metric
    Δ = a2 + r2 - 2*r
    Σ = a2*c_θ2 + r2
    Σ2 = Σ * Σ
    Σ4 = Σ2 * Σ2

    dx[1] = sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4))/((a2 + r*(r - 2))*Σ)

    dx[2] = p_r*(a2 + r*(r - 2))/Σ

    dx[3] = p_θ/Σ

    dx[4] = (a2*p_ϕ/t_θ2 + 2*a*r*(4*a*p_ϕ*r + 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))/(2*a4*c_θ2 + r*(a2*c_2θ*r + 3*a2*r + 4*a2*s_θ2 + 2*r3)) + p_ϕ*r*(r - 2)/s_θ2)/((a2 + r*(r - 2))*Σ)

    dx[5] = (2*a*s_θ2*Σ*(-4*a*p_ϕ*r - 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))*(2*a4*c_θ2 - a2*r2*(c_2θ + 3) - 6*r4)*(a2*p_ϕ/t_θ2 + 2*a*r*(4*a*p_ϕ*r + 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))/(2*a4*c_θ2 + r*(a2*c_2θ*r + 3*a2*r + 4*a2*s_θ2 + 2*r3)) + p_ϕ*r*(r - 2)/s_θ2)/((a2 + r*(r - 2))^2*(2*a4*c_θ2 + a2*r2*(c_2θ + 3) + 4*a2*r*s_θ2 + 2*r4)) + 4*p_r2*Σ2*(a2*c_θ2 + r*(a2*s_θ2 - r)) + 4*p_θ2*r*Σ2 + 4*p_ϕ*Σ*(-a3*c_θ2 + a*r2)*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4))/(a2 + r*(r - 2))^2 + 2*p_ϕ*(a2*p_ϕ/t_θ2 + 2*a*r*(4*a*p_ϕ*r + 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))/(2*a4*c_θ2 + r*(a2*c_2θ*r + 3*a2*r + 4*a2*s_θ2 + 2*r3)) + p_ϕ*r*(r - 2)/s_θ2)*(2*a6*c_θ6*r + 6*a2*c_θ2*r5 + 2*c_θ4*(a6*s_θ2 + a4*r2*(3*r - 2)) + r4*(-3*a2*c_2θ - 5*a2 + r2*(2*r - 4)))/(a2 + r*(r - 2))^2 + (4*a2 + 4*r2)*(a2*c_θ2 - r2)*Σ*(4*a*p_ϕ*r + 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4))/((a2 + r*(r - 2))^2*(2*a4*c_θ2 + r*(a2*c_2θ*r + 3*a2*r + 4*a2*s_θ2 + 2*r3))))/(4*Σ4)

    dx[6] = (4*a3*c_θ*r*s_θ3*Σ*(-4*a*p_ϕ*r - 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))*(a2*p_ϕ/t_θ2 + 2*a*r*(4*a*p_ϕ*r + 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))/(2*a4*c_θ2 + r*(a2*c_2θ*r + 3*a2*r + 4*a2*s_θ2 + 2*r3)) + p_ϕ*r*(r - 2)/s_θ2)/((a2 + r*(r - 2))*(2*a4*c_θ2 + a2*r2*(c_2θ + 3) + 4*a2*r*s_θ2 + 2*r4)) - a2*p_r2*s_2θ*(a2 + r*(r - 2))*Σ2 - a2*p_θ2*s_2θ*Σ2 + 2*a2*r*s_2θ*Σ*(4*a*p_ϕ*r + 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4))/((a2 + r*(r - 2))*(2*a4*c_θ2 + r*(a2*c_2θ*r + 3*a2*r + 4*a2*s_θ2 + 2*r3))) - 4*a*p_ϕ*r*Σ*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4))/(t_θ*(a2 + r*(r - 2))) + p_ϕ*(a2*p_ϕ/t_θ2 + 2*a*r*(4*a*p_ϕ*r + 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*(a2 + r*(r - 2))^2 + p_θ2*(a2 + r*(r - 2)) + p_ϕ2*(a2*c_θ2 + r*(r - 2))/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))/(2*a4*c_θ2 + r*(a2*c_2θ*r + 3*a2*r + 4*a2*s_θ2 + 2*r3)) + p_ϕ*r*(r - 2)/s_θ2)*(2*a6*c_θ6 + 6*a4*c_θ4*r2 + a4*r*s_2θ2 + 6*a2*c_θ2*r4 + 4*a2*r3*s_θ2 + 2*r6)/(t_θ*(a2 + r*(r - 2))))/(2*Σ4)

    dx[7] = 0.0

    return nothing
end


"""
    gshe_odes!(
        dx::Vector{<:Real},
        x::Vector{<:Real},
        geometry::Geometry,
        ϵ::Real,
        s::Integer
    )

Calculate the derivatives of ``x^μ`` and ``p_i`` with respect to the proper time, returned
in this order. Expresssions are exported from Mathematica.
"""
@fastmath function gshe_odes!(
    dx::Vector{<:Real},
    x::Vector{<:Real},
    geometry::Geometry,
    ϵ::Real,
    s::Integer
)
    # Unpack the coordinates
    r, θ, ϕ, p_r, p_θ, p_ϕ = @view x[2:end]
    @unpack a = geometry

    # Precompute basic trigonometric functions.
    s_θ = sin(θ)
    c_θ = cos(θ)

    # Precompute further trigonometric functions using trigonometric identities.
    s_2θ = 2 * s_θ * c_θ
    c_2θ = c_θ * c_θ - s_θ * s_θ
    t_θ = s_θ / c_θ
    t_θ2 = t_θ * t_θ

    v3 = tetrad_boosting(r, geometry)
    dv3 = derivative_tetrad_boosting(r, geometry)

    # Precompute powers of the BH spin
    a2 = a * a
    a3 = a * a2
    a4 = a * a3
    a6 = a3 * a3

    # Precompute powers of the coordinates
    r2 = r * r
    r3 = r2 * r
    r4 = r2 * r2
    r5 = r4 * r
    r6 = r3 * r3
    r7 = r6 * r

    s_θ2 = s_θ * s_θ
    s_θ3 = s_θ2 * s_θ
    s_2θ2 = s_2θ * s_2θ

    c_θ2 = c_θ * c_θ
    c_θ4 = c_θ2 * c_θ2
    c_θ6 = c_θ4 * c_θ2

    p_r2 = p_r * p_r
    p_θ2 = p_θ * p_θ
    p_ϕ2 = p_ϕ * p_ϕ

    # Various functions appearing in the Kerr metric
    Δ = a2 + r2 - 2*r
    Δ2 = Δ * Δ
    Σ = a2*c_θ2 + r2
    Σ2 = Σ * Σ
    Σ3 = Σ2 * Σ
    Σ3H = Σ * sqrt(Σ)
    Σ4 = Σ2 * Σ2
    Σ5 = Σ4 * Σ
    sqrtΣ = sqrt(Σ)
    sqrtΣΔ = sqrt(Σ*Δ)


    # Particularly, these powers of 3/2 are expensive to evaluate, hence
    # best to precompute them.
    ΣΔ3H = (Σ*Δ)^(3/2)
    s_θ2Σ23H = (s_θ2*Σ2)^(3/2)

    a2r2 = a2 + r2
    a2r2_2 = a2r2 * a2r2

    v3 = tetrad_boosting(r, geometry)
    dv3 = derivative_tetrad_boosting(r, geometry)

    v3_form1H = sqrt(1 - v3^2)
    v3_form3H = (1 - v3^2)^(3/2)
    v3_altform3H = (-(v3^2 - 1)*Δ)^(3/2)
    v3altfrom1H = sqrt(-(v3^2 - 1)*Δ)

    dx[1] = ϵ*(-p_r*s*s_θ2*(a*s_θ*sqrtΣ*Δ + v3*sqrtΣΔ*a2r2)*(2*a*r*Σ*sqrt(4*a2*p_ϕ2*r2 + (p_r2*Δ2 + p_θ2*Δ + p_ϕ2*(a2*c_θ2 + r2 - 2*r)/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4))*(-a*s_2θ*(a*s_θ*v3*sqrtΣΔ + sqrtΣ*Δ)/v3altfrom1H + 2*c_θ*s_θ*(-2*a*r*(a*s_θ*v3/sqrtΣ + a2r2/sqrtΣΔ) + (a/sqrtΣΔ + v3/(s_θ*sqrtΣ))*(-a2*s_θ2*Δ + a2r2_2))/v3_form1H) + c_θ*s_θ*(-2*a*r*(-4*a*p_ϕ*r - 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*Δ2 + p_θ2*Δ + p_ϕ2*(a2*c_θ2 + r2 - 2*r)/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))/(2*a4*c_θ2 + a2*r2*(c_2θ + 3) + 4*a2*r*s_θ2 + 2*r4) + p_ϕ*(a2/t_θ2 + r2/s_θ2 - 2*r/s_θ2))*(4*a3*r*s_θ2*Σ*(a*s_θ*v3*sqrtΣΔ + sqrtΣ*Δ)/v3altfrom1H - (-2*a*r*(a*s_θ*v3/sqrtΣ + a2r2/sqrtΣΔ) + (a/sqrtΣΔ + v3/(s_θ*sqrtΣ))*(-a2*s_θ2*Δ + a2r2_2))*(2*a6*c_θ6 + 6*a4*c_θ4*r2 + a4*r*s_2θ2 + 6*a2*c_θ2*r4 + 4*a2*r3*s_θ2 + 2*r6)/v3_form1H))/(sqrt(s_θ2*Σ2)*Σ5*(p_ϕ*(a*s_θ*sqrtΣ + v3*sqrtΣΔ) + s_θ*(a*(a*sqrtΣ + s_θ*v3*sqrtΣΔ) + r2*sqrtΣ)*(-4*a*p_ϕ*r - 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*Δ2 + p_θ2*Δ + p_ϕ2*(a2*c_θ2 + r2 - 2*r)/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))/(2*a4*c_θ2 + a2*r2*(c_2θ + 3) + 4*a2*r*s_θ2 + 2*r4))*(2*a2 + 2*r2 - 4*r)) + p_θ*s*s_θ2*(2*s_θ2*(-2*a*r*(-4*a*p_ϕ*r - 2*sqrt(4*a2*p_ϕ2*r2 + (p_r2*Δ2 + p_θ2*Δ + p_ϕ2*(a2*c_θ2 + r2 - 2*r)/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4)))/(2*a4*c_θ2 + a2*r2*(c_2θ + 3) + 4*a2*r*s_θ2 + 2*r4) + p_ϕ*(a2/t_θ2 + r2/s_θ2 - 2*r/s_θ2))*(a*Σ*(a*s_θ*v3*sqrtΣΔ + sqrtΣ*Δ)*(2*a4*c_θ2 - a2*r2*(c_2θ + 3) - 6*r4)/v3altfrom1H - (-2*a*r*(a*s_θ*v3/sqrtΣ + a2r2/sqrtΣΔ) + (a/sqrtΣΔ + v3/(s_θ*sqrtΣ))*(-a2*s_θ2*Δ + a2r2_2))*(2*a6*c_θ6*r + 2*a6*c_θ4*s_θ2 + 6*a4*c_θ4*r3 - 4*a4*c_θ4*r2 + 6*a2*c_θ2*r5 - a2*r4*(3*c_2θ + 5) + 2*r7 - 4*r6)/v3_form1H) + (-2*a2*c_θ2 - 2*r2)*sqrt(4*a2*p_ϕ2*r2 + (p_r2*Δ2 + p_θ2*Δ + p_ϕ2*(a2*c_θ2 + r2 - 2*r)/s_θ2)*(a4*c_θ2 + a2*r2*(c_2θ + 3)/2 + 2*a2*r*s_θ2 + r4))*(s_θ2*(-2*a3*c_θ2 + 2*a*r2)*(-2*a*r*(a*s_θ*v3/sqrtΣ + a2r2/sqrtΣΔ) + (a/sqrtΣΔ + v3/(s_θ*sqrtΣ))*(-a2*s_θ2*Δ + a2r2_2))/v3_form1H + (2*a2 + 2*r2)